const amis = [[0, 0, 0], [1, 0, 0], [0, 1, 0], [0, 0, 1]]
const N_0_0_0 = 1
const N_1_0_0 = 2
const N_0_1_0 = 3
const N_0_0_1 = 4
function unnormalized_overlap!(g::STV, S::Matrix{Float64})
    if g.la == 0 && g.lb == 0
        overlap_0_0!(g, S)
    elseif g.la == 0 && g.lb == 1
        overlap_0_1!(g, S)
    elseif g.la == 1 && g.lb == 0
        overlap_1_0!(g, S)
    elseif g.la == 1 && g.lb == 1
        overlap_1_1!(g, S)
    else
        error()
    end
end
function unnormalized_kinetic!(g::STV, S::Matrix{Float64}, T::Matrix{Float64})
    if g.la == 0 && g.lb == 0
        kinetic_0_0!(g, S, T)
    elseif g.la == 0 && g.lb == 1
        kinetic_0_1!(g, S, T)
    elseif g.la == 1 && g.lb == 0
        kinetic_1_0!(g, S, T)
    elseif g.la == 1 && g.lb == 1
        kinetic_1_1!(g, S, T)
    else
        error()
    end
end
function unnormalized_nuclear!(g::STV, V::Matrix{Float64})
    if g.la == 0 && g.lb == 0
        nuclear_0_0!(g, V)
    elseif g.la == 0 && g.lb == 1
        nuclear_0_1!(g, V)
    elseif g.la == 1 && g.lb == 0
        nuclear_1_0!(g, V)
    elseif g.la == 1 && g.lb == 1
        nuclear_1_1!(g, V)
    else
        error()
    end
end
function unnormalized_eri!(g::Eri, eri::Array{Float64, 4})
    if g.la == 0 && g.lb == 0 && g.lc == 0 && g.ld == 0
