function overlap_0_0!(g::STV, S::Matrix{Float64})
    S[N_0_0_0, N_0_0_0] = g.overlap_ss
end
function overlap_0_1!(g::STV, S::Matrix{Float64})
    S[N_0_0_0, N_0_0_0] = g.overlap_ss
    S[N_0_0_0, N_1_0_0] = S[N_0_0_0, N_0_0_0]*g.BP[1]
    S[N_0_0_0, N_0_1_0] = S[N_0_0_0, N_0_0_0]*g.BP[2]
    S[N_0_0_0, N_0_0_1] = S[N_0_0_0, N_0_0_0]*g.BP[3]
end
function overlap_1_0!(g::STV, S::Matrix{Float64})
    S[N_0_0_0, N_0_0_0] = g.overlap_ss
    S[N_1_0_0, N_0_0_0] = S[N_0_0_0, N_0_0_0]*g.AP[1]
    S[N_0_1_0, N_0_0_0] = S[N_0_0_0, N_0_0_0]*g.AP[2]
    S[N_0_0_1, N_0_0_0] = S[N_0_0_0, N_0_0_0]*g.AP[3]
end
function overlap_1_1!(g::STV, S::Matrix{Float64})
    S[N_0_0_0, N_0_0_0] = g.overlap_ss
    S[N_0_0_0, N_1_0_0] = S[N_0_0_0, N_0_0_0]*g.BP[1]
    S[N_0_0_0, N_0_1_0] = S[N_0_0_0, N_0_0_0]*g.BP[2]
    S[N_0_0_0, N_0_0_1] = S[N_0_0_0, N_0_0_0]*g.BP[3]
    S[N_1_0_0, N_0_0_0] = S[N_0_0_0, N_0_0_0]*g.AP[1]
    S[N_1_0_0, N_1_0_0] = S[N_0_0_0, N_0_0_0]*g.oo2z + S[N_0_0_0, N_1_0_0]*g.AP[1]
    S[N_1_0_0, N_0_1_0] = S[N_0_0_0, N_0_1_0]*g.AP[1]
    S[N_1_0_0, N_0_0_1] = S[N_0_0_0, N_0_0_1]*g.AP[1]
    S[N_0_1_0, N_0_0_0] = S[N_0_0_0, N_0_0_0]*g.AP[2]
    S[N_0_1_0, N_1_0_0] = S[N_0_0_0, N_1_0_0]*g.AP[2]
    S[N_0_1_0, N_0_1_0] = S[N_0_0_0, N_0_0_0]*g.oo2z + S[N_0_0_0, N_0_1_0]*g.AP[2]
    S[N_0_1_0, N_0_0_1] = S[N_0_0_0, N_0_0_1]*g.AP[2]
    S[N_0_0_1, N_0_0_0] = S[N_0_0_0, N_0_0_0]*g.AP[3]
    S[N_0_0_1, N_1_0_0] = S[N_0_0_0, N_1_0_0]*g.AP[3]
    S[N_0_0_1, N_0_1_0] = S[N_0_0_0, N_0_1_0]*g.AP[3]
    S[N_0_0_1, N_0_0_1] = S[N_0_0_0, N_0_0_0]*g.oo2z + S[N_0_0_0, N_0_0_1]*g.AP[3]
end
function kinetic_0_0!(g::STV, S::Matrix{Float64}, T::Matrix{Float64})
    T[N_0_0_0, N_0_0_0] = g.kinetic_ss
end
function kinetic_0_1!(g::STV, S::Matrix{Float64}, T::Matrix{Float64})
    T[N_0_0_0, N_0_0_0] = g.kinetic_ss
    T[N_0_0_0, N_1_0_0] = 2*S[N_0_0_0, N_1_0_0]*g.xi + T[N_0_0_0, N_0_0_0]*g.BP[1]
    T[N_0_0_0, N_0_1_0] = 2*S[N_0_0_0, N_0_1_0]*g.xi + T[N_0_0_0, N_0_0_0]*g.BP[2]
    T[N_0_0_0, N_0_0_1] = 2*S[N_0_0_0, N_0_0_1]*g.xi + T[N_0_0_0, N_0_0_0]*g.BP[3]
end
function kinetic_1_0!(g::STV, S::Matrix{Float64}, T::Matrix{Float64})
    T[N_0_0_0, N_0_0_0] = g.kinetic_ss
    T[N_1_0_0, N_0_0_0] = 2*S[N_1_0_0, N_0_0_0]*g.xi + T[N_0_0_0, N_0_0_0]*g.AP[1]
    T[N_0_1_0, N_0_0_0] = 2*S[N_0_1_0, N_0_0_0]*g.xi + T[N_0_0_0, N_0_0_0]*g.AP[2]
    T[N_0_0_1, N_0_0_0] = 2*S[N_0_0_1, N_0_0_0]*g.xi + T[N_0_0_0, N_0_0_0]*g.AP[3]
end
function kinetic_1_1!(g::STV, S::Matrix{Float64}, T::Matrix{Float64})
    T[N_0_0_0, N_0_0_0] = g.kinetic_ss
    T[N_0_0_0, N_1_0_0] = 2*S[N_0_0_0, N_1_0_0]*g.xi + T[N_0_0_0, N_0_0_0]*g.BP[1]
    T[N_0_0_0, N_0_1_0] = 2*S[N_0_0_0, N_0_1_0]*g.xi + T[N_0_0_0, N_0_0_0]*g.BP[2]
    T[N_0_0_0, N_0_0_1] = 2*S[N_0_0_0, N_0_0_1]*g.xi + T[N_0_0_0, N_0_0_0]*g.BP[3]
    T[N_1_0_0, N_0_0_0] = 2*S[N_1_0_0, N_0_0_0]*g.xi + T[N_0_0_0, N_0_0_0]*g.AP[1]
    T[N_1_0_0, N_1_0_0] = 2*S[N_1_0_0, N_1_0_0]*g.xi + T[N_0_0_0, N_0_0_0]*g.oo2z + T[N_0_0_0, N_1_0_0]*g.AP[1]
    T[N_1_0_0, N_0_1_0] = 2*S[N_1_0_0, N_0_1_0]*g.xi + T[N_0_0_0, N_0_1_0]*g.AP[1]
    T[N_1_0_0, N_0_0_1] = 2*S[N_1_0_0, N_0_0_1]*g.xi + T[N_0_0_0, N_0_0_1]*g.AP[1]
    T[N_0_1_0, N_0_0_0] = 2*S[N_0_1_0, N_0_0_0]*g.xi + T[N_0_0_0, N_0_0_0]*g.AP[2]
    T[N_0_1_0, N_1_0_0] = 2*S[N_0_1_0, N_1_0_0]*g.xi + T[N_0_0_0, N_1_0_0]*g.AP[2]
    T[N_0_1_0, N_0_1_0] = 2*S[N_0_1_0, N_0_1_0]*g.xi + T[N_0_0_0, N_0_0_0]*g.oo2z + T[N_0_0_0, N_0_1_0]*g.AP[2]
    T[N_0_1_0, N_0_0_1] = 2*S[N_0_1_0, N_0_0_1]*g.xi + T[N_0_0_0, N_0_0_1]*g.AP[2]
    T[N_0_0_1, N_0_0_0] = 2*S[N_0_0_1, N_0_0_0]*g.xi + T[N_0_0_0, N_0_0_0]*g.AP[3]
    T[N_0_0_1, N_1_0_0] = 2*S[N_0_0_1, N_1_0_0]*g.xi + T[N_0_0_0, N_1_0_0]*g.AP[3]
    T[N_0_0_1, N_0_1_0] = 2*S[N_0_0_1, N_0_1_0]*g.xi + T[N_0_0_0, N_0_1_0]*g.AP[3]
    T[N_0_0_1, N_0_0_1] = 2*S[N_0_0_1, N_0_0_1]*g.xi + T[N_0_0_0, N_0_0_0]*g.oo2z + T[N_0_0_0, N_0_0_1]*g.AP[3]
end
function nuclear_0_0!(g::STV, V::Matrix{Float64})
    V[N_0_0_0, N_0_0_0] = g.nuclear_ss[1]
end
function nuclear_0_1!(g::STV, V::Matrix{Float64})
    V[N_0_0_0, N_0_0_0] = g.nuclear_ss[1]
    V_0_0_0_0_0_0_1 = g.nuclear_ss[2]
    V[N_0_0_0, N_0_0_1] = V[N_0_0_0, N_0_0_0]*g.BP[3] - V_0_0_0_0_0_0_1*g.CP[3]
    V[N_0_0_0, N_0_1_0] = V[N_0_0_0, N_0_0_0]*g.BP[2] - V_0_0_0_0_0_0_1*g.CP[2]
    V[N_0_0_0, N_1_0_0] = V[N_0_0_0, N_0_0_0]*g.BP[1] - V_0_0_0_0_0_0_1*g.CP[1]
end
function nuclear_1_0!(g::STV, V::Matrix{Float64})
    V[N_0_0_0, N_0_0_0] = g.nuclear_ss[1]
    V_0_0_0_0_0_0_1 = g.nuclear_ss[2]
    V[N_0_0_1, N_0_0_0] = V[N_0_0_0, N_0_0_0]*g.AP[3] - V_0_0_0_0_0_0_1*g.CP[3]
    V[N_0_1_0, N_0_0_0] = V[N_0_0_0, N_0_0_0]*g.AP[2] - V_0_0_0_0_0_0_1*g.CP[2]
    V[N_1_0_0, N_0_0_0] = V[N_0_0_0, N_0_0_0]*g.AP[1] - V_0_0_0_0_0_0_1*g.CP[1]
end
function nuclear_1_1!(g::STV, V::Matrix{Float64})
    V[N_0_0_0, N_0_0_0] = g.nuclear_ss[1]
    V_0_0_0_0_0_0_1 = g.nuclear_ss[2]
    V_0_0_0_0_0_0_2 = g.nuclear_ss[3]
    V[N_0_0_0, N_0_0_1] = V[N_0_0_0, N_0_0_0]*g.BP[3] - V_0_0_0_0_0_0_1*g.CP[3]
    V_0_0_0_0_0_1_1 = V_0_0_0_0_0_0_1*g.BP[3] - V_0_0_0_0_0_0_2*g.CP[3]
    V[N_0_0_0, N_0_1_0] = V[N_0_0_0, N_0_0_0]*g.BP[2] - V_0_0_0_0_0_0_1*g.CP[2]
    V_0_0_0_0_1_0_1 = V_0_0_0_0_0_0_1*g.BP[2] - V_0_0_0_0_0_0_2*g.CP[2]
    V[N_0_0_0, N_1_0_0] = V[N_0_0_0, N_0_0_0]*g.BP[1] - V_0_0_0_0_0_0_1*g.CP[1]
    V_0_0_0_1_0_0_1 = V_0_0_0_0_0_0_1*g.BP[1] - V_0_0_0_0_0_0_2*g.CP[1]
    V[N_0_0_1, N_0_0_0] = V[N_0_0_0, N_0_0_0]*g.AP[3] - V_0_0_0_0_0_0_1*g.CP[3]
    V[N_0_0_1, N_0_0_1] = V[N_0_0_0, N_0_0_1]*g.AP[3] - V_0_0_0_0_0_1_1*g.CP[3] + g.oo2z*(V[N_0_0_0, N_0_0_0] - V_0_0_0_0_0_0_1)
    V[N_0_0_1, N_0_1_0] = V[N_0_0_0, N_0_1_0]*g.AP[3] - V_0_0_0_0_1_0_1*g.CP[3]
    V[N_0_0_1, N_1_0_0] = V[N_0_0_0, N_1_0_0]*g.AP[3] - V_0_0_0_1_0_0_1*g.CP[3]
    V[N_0_1_0, N_0_0_0] = V[N_0_0_0, N_0_0_0]*g.AP[2] - V_0_0_0_0_0_0_1*g.CP[2]
    V[N_0_1_0, N_0_0_1] = V[N_0_0_0, N_0_0_1]*g.AP[2] - V_0_0_0_0_0_1_1*g.CP[2]
    V[N_0_1_0, N_0_1_0] = V[N_0_0_0, N_0_1_0]*g.AP[2] - V_0_0_0_0_1_0_1*g.CP[2] + g.oo2z*(V[N_0_0_0, N_0_0_0] - V_0_0_0_0_0_0_1)
    V[N_0_1_0, N_1_0_0] = V[N_0_0_0, N_1_0_0]*g.AP[2] - V_0_0_0_1_0_0_1*g.CP[2]
    V[N_1_0_0, N_0_0_0] = V[N_0_0_0, N_0_0_0]*g.AP[1] - V_0_0_0_0_0_0_1*g.CP[1]
    V[N_1_0_0, N_0_0_1] = V[N_0_0_0, N_0_0_1]*g.AP[1] - V_0_0_0_0_0_1_1*g.CP[1]
    V[N_1_0_0, N_0_1_0] = V[N_0_0_0, N_0_1_0]*g.AP[1] - V_0_0_0_0_1_0_1*g.CP[1]
    V[N_1_0_0, N_1_0_0] = V[N_0_0_0, N_1_0_0]*g.AP[1] - V_0_0_0_1_0_0_1*g.CP[1] + g.oo2z*(V[N_0_0_0, N_0_0_0] - V_0_0_0_0_0_0_1)
end
function eri_0_0_0_0!(g::Eri, eri::Array{Float64, 4})
    eri[N_0_0_0, N_0_0_0, N_0_0_0, N_0_0_0] = g.ssss[1]
end
function eri_0_0_0_1!(g::Eri, eri::Array{Float64, 4})
    eri[N_0_0_0, N_0_0_0, N_0_0_0, N_0_0_0] = g.ssss[1]
    eri_0_0_0_0_0_0_0_0_0_0_0_0_1 = g.ssss[2]
    eri[N_0_0_0, N_0_0_0, N_0_0_0, N_0_0_1] = eri[N_0_0_0, N_0_0_0, N_0_0_0, N_0_0_0]*g.DQ[3] + eri_0_0_0_0_0_0_0_0_0_0_0_0_1*g.QW[3]
    eri[N_0_0_0, N_0_0_0, N_0_0_0, N_0_1_0] = eri[N_0_0_0, N_0_0_0, N_0_0_0, N_0_0_0]*g.DQ[2] + eri_0_0_0_0_0_0_0_0_0_0_0_0_1*g.QW[2]
    eri[N_0_0_0, N_0_0_0, N_0_0_0, N_1_0_0] = eri[N_0_0_0, N_0_0_0, N_0_0_0, N_0_0_0]*g.DQ[1] + eri_0_0_0_0_0_0_0_0_0_0_0_0_1*g.QW[1]
end
function eri_0_0_1_0!(g::Eri, eri::Array{Float64, 4})
    eri[N_0_0_0, N_0_0_0, N_0_0_0, N_0_0_0] = g.ssss[1]
    eri_0_0_0_0_0_0_0_0_0_0_0_0_1 = g.ssss[2]
