    eri[N_0_0_0, N_0_0_0, N_0_1_0, N_0_0_0] = eri[N_0_0_0, N_0_0_0, N_0_0_0, N_0_0_0]*g.CQ[2] + eri_0_0_0_0_0_0_0_0_0_0_0_0_1*g.QW[2]
    eri[N_0_0_0, N_0_0_0, N_1_0_0, N_0_0_0] = eri[N_0_0_0, N_0_0_0, N_0_0_0, N_0_0_0]*g.CQ[1] + eri_0_0_0_0_0_0_0_0_0_0_0_0_1*g.QW[1]
end
function eri_0_0_1_1!(g::Eri, eri::Array{Float64, 4})
    eri[N_0_0_0, N_0_0_0, N_0_0_0, N_0_0_0] = g.ssss[1]
    eri_0_0_0_0_0_0_0_0_0_0_0_0_1 = g.ssss[2]
    eri_0_0_0_0_0_0_0_0_0_0_0_0_2 = g.ssss[3]
    eri[N_0_0_0, N_0_0_0, N_0_0_0, N_0_0_1] = eri[N_0_0_0, N_0_0_0, N_0_0_0, N_0_0_0]*g.DQ[3] + eri_0_0_0_0_0_0_0_0_0_0_0_0_1*g.QW[3]
    eri_0_0_0_0_0_0_0_0_0_0_0_1_1 = eri_0_0_0_0_0_0_0_0_0_0_0_0_1*g.DQ[3] + eri_0_0_0_0_0_0_0_0_0_0_0_0_2*g.QW[3]
    eri[N_0_0_0, N_0_0_0, N_0_0_0, N_0_1_0] = eri[N_0_0_0, N_0_0_0, N_0_0_0, N_0_0_0]*g.DQ[2] + eri_0_0_0_0_0_0_0_0_0_0_0_0_1*g.QW[2]
